import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
    },
    backgroundColor: "#1C0C5B",
    color: 'white',
  },
  paper: {
    padding: theme.spacing(2),
    backgroundColor: "#1C0C5B",
  },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    color: "#fff",
  },
  fileInput: {
    width: '97%',
    margin: '10px 0',

  },
  buttonSubmit: {
    marginBottom: 10,
  },
}));
